import statisticsdata = "100,30,53,67,85,87,50,45,51,72,64,69,59,17,22,23,44,25,16,67,85,87,50,45,51,72,59,14,50,55,32,23,24,25,37,28,39,30,33,35,40,34,41,43,94,95,59,98,99,44,45,47,48,49,53,61,63,69,75,77,60,83" gradelist = data.split(",")gradelist = list(map(int,gradelist))print(gradelist)mingrade = (min(gradelist))print("Min grade is :", mingrade)maxgrade = (max(gradelist))print("Max grade is :", maxgrade)gradelength = len(gradelist)totalsum = sum(gradelist)averagesum = totalsum / gradelengthroundedgrade = round(averagesum, 2)print("Grade length" , gradelength)print("Total Sum", totalsum)print("Average Sum", averagesum)print("Rounded Sum", roundedgrade)meangrade = statistics.mean(gradelist)meanrounded = round(meangrade, 2)print("Mean grade is:", meangrade)print("Mean grade rounded is:", meanrounded)mediangrade = statistics.median(gradelist)print("Median grade is:", mediangrade)formattedstring = "The min grade is {}, the max grade is {}, the average is {}, the mean is {} and the median is {}".format(mingrade,maxgrade,roundedgrade,meanrounded,mediangrade)print(formattedstring)